#! /usr/bin/perl

use strict;
use warnings;

my $seq1 ="";
my $seq2 ="";

my @seq_a = split(//,$seq1);

my @seq_b = split(//, $seq2);

my $count = 0;
for (my $i = 0; $i <= scalar(@seq_a); $i++){
    
    if ($seq_a[$i] ne $seq_b[$i] ){
        $count=$count+ 1;
    }
    
}

print "score is $count\n";